import close from "../image/close.svg";
import React, { useEffect, useRef } from 'react';
import FormValidator from '../utils/FormValidator';  // Importe a classe FormValidator

export default function PopupWithForm({ isOpen, onClose, formConfig, children, onSubmit }) {
  const formRef = useRef(); // Ref para o formulário

  useEffect(() => {
    // Inicializa o FormValidator quando o popup estiver aberto
    if (isOpen) {
      const validator = new FormValidator(formConfig, formRef.current);
      validator.enableValidation();  // Habilita a validação

      return () => {
        validator.resetValidation();  // Reseta a validação ao fechar o popup
      };
    }
  }, [isOpen, formConfig]);  // Quando o popup abrir/fechar, reinicia a validação

  // Handle submit do formulário
  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit();  // Chama o onSubmit passado pelo componente pai
  };

  const cssIsOpened = isOpen ? 'popup-opened' : '';

  return (
    <div className={`popup popup_${name} ${cssIsOpened}`}>
          <div className="overlay"></div>
          <div className="popup__container">
            <button className="popup__close" onClick={onClose}><img src={close} alt="icone de fechar"
                className="popup__close-img"/></button>
            <h2 className="form__title">{title}</h2>
            <form className="form__fieldset" noValidate name={name} onSubmit={handleSubmit}>
              {children}
            </form>
          </div>
        </div>
  )
}
