import { useEffect, useRef } from "react";
import close from "../../images/close.svg";
import FormValidator from "../../utils/FormValidator"; // Ajuste o caminho conforme necessário

// Configurações para cada tipo de formulário
const configUserValidate = { 
  inputSelector: ".form__input-name, .form__input-job",  // Ajuste o seletor para os inputs de nome e descrição
  submitButtonSelector: ".form__submit", 
  inputErrorClass: "form__input-error", 
  errorClassVisible: "form__input-error-visible",
  inactiveButtonClass: "form__submit_disabled"
};

// Configuração para o formulário de card (título e link)
const configFormValidade = {
  inputSelector: ".formcard__input-title, .formcard__input-link", // Ajuste o seletor para os inputs de título e link
  submitButtonSelector: ".formcard__submit", 
  inputErrorClass: "formcard__input-error", 
  errorClassVisible: "formcard__input-error-visible",
  inactiveButtonClass: "formcard__submit_disabled"
};

// Configuração para o formulário de avatar
const configAvatarValidade = {
  inputSelector: ".popup__avatar-input",  // Ajuste o seletor para o input da URL do avatar
  submitButtonSelector: ".popup__avatar-submit", 
  inputErrorClass: "popup__avatar-input-error", 
  errorClassVisible: "popup__avatar-input-error-visible",
  inactiveButtonClass: "popup__avatar-submit_disabled"
};

export default function PopupWithForm({ name, title, children, isOpen, onClose, onSubmit }) {
  const formRef = useRef();
  const validatorRef = useRef();

  // Função para definir a configuração de validação com base no tipo de popup
  const getConfig = () => {
    if (name === "user") return configUserValidate;
    if (name === "card") return configFormValidade;
    if (name === "avatar") return configAvatarValidade;
    return null; // Garante retorno seguro
  };

  useEffect(() => {
    const config = getConfig();
    if (isOpen && formRef.current && config) {
      validatorRef.current = new FormValidator(config, formRef.current);
      validatorRef.current.enableValidation();
      validatorRef.current.resetValidation(); // Limpa mensagens de erro e define estado inicial do botão
    }
  }, [isOpen, name]);

  const handleSubmit = (event) => {
    event.preventDefault();
    onSubmit(event);
  };

  const cssIsOpened = isOpen ? "popup-opened" : "";

  return (
    <div className={`popup popup_${name} ${cssIsOpened}`}>
      <div className="overlay"></div>
      <div className="popup__container">
        <button className="popup__close" onClick={onClose}>
          <img src={close} alt="icone de fechar" className="popup__close-img" />
        </button>
        <h2 className="form__title">{title}</h2>
        <form
          className="form__fieldset"
          noValidate
          name={name}
          onSubmit={handleSubmit}
          ref={formRef}
        >
          {children}
          <button type="submit" className={getConfig()?.submitButtonSelector || ''}>Salvar</button>
        </form>
      </div>
    </div>
  );
}
