import close from "../image/close.svg";
import { useEffect, useRef } from 'react';
import FormValidator from '../utils/FormValidator';

const configUserValidate = {
  inputSelectorOne: ".form__input-name",
  inputSelectorTwo: ".form__input-job",
  submitButtonSelector: ".form__submit",
  errorClassOne: ".form__input-name-error",
  errorClassTwo: ".form__input-job-error"
};

const configFormValidade = {
  inputSelectorOne: ".formcard__input-title",
  inputSelectorTwo: ".formcard__input-link",
  submitButtonSelector: ".formcard__submit",
  errorClassOne: ".formcard__input-title-error",
  errorClassTwo: ".formcard__input-link-error"
};

const configAvatarValidade = {
  inputSelectorTwo: ".popup__avatar-input",
  submitButtonSelector: ".popup__avatar-submit",
  errorClassTwo: ".popup__avatar-input-error"
};

export default function PopupWithForm({ isOpen, onClose, onSubmit, title, name, button, children }) {
  const formRef = useRef(null);

  useEffect(() => {
    if (isOpen && formRef.current) {
      let formValidatorInstance;

      if (name === "userForm") {
        formValidatorInstance = new FormValidator(configUserValidate, formRef.current);
      } else if (name === "formCard") {
        formValidatorInstance = new FormValidator(configFormValidade, formRef.current);
      } else if (name === "formAvatar") {
        formValidatorInstance = new FormValidator(configAvatarValidade, formRef.current);
      }

      formValidatorInstance.enableValidation();

      return () => formValidatorInstance.resetValidation();  // Reseta a validação ao fechar o popup
    }
  }, [isOpen, name]);

  const handleSubmit = (event) => {
    event.preventDefault();
    onSubmit(event);
  }
  const cssIsOpened = isOpen ? 'popup-opened' : '';

  return (
    <div className={`popup popup_${name} ${cssIsOpened}`}>
          <div className="overlay"></div>
          <div className="popup__container">
            <button className="popup__close" onClick={onClose}><img src={close} alt="icone de fechar"
                className="popup__close-img"/></button>
            <h2 className="form__title">{title}</h2>
            <form ref={formRef} className="form__fieldset" noValidate name={name} onSubmit={handleSubmit}>
              {children}
            </form>
          </div>
        </div>
  )
}
