import { useState, useContext, useEffect } from 'react'; 
import { CurrentUserContext } from '../contexts/CurrentUserContext'; 
import PopupWithForm from './PopupWithForm';

export default function EditProfile({ isOpen, onClose, onUpdtateUser }) {
  const currentUser = useContext(CurrentUserContext); // Obtém o objeto de usuário atual
  const [name, setName] = useState(""); // Adicione variável de estado para nome
  const [about, setAbout] = useState(""); // Adicione variável de estado para descrição

  useEffect(() => {
    setName(currentUser.name);
    setAbout(currentUser.about);
  }, [currentUser]);

  const handleSubmit = (e) => {
    e.preventDefautl();
    onUpdtateUser({
      name,
      about,
    });
  };

  return (
    <PopupWithForm
      isOpen={isOpen}
      onClose={onClose}
      onSubmit={handleSubmit}
      className="popup__form"
      name="profile-form"
      id="edit-profile-form"
      noValidate
    >
      <label className="popup__label">
        <input
          className="form__input-name"
          maxLength={40}
          minLength={2}
          name="name"
          placeholder="Nome"
          required
          type="text"
          value={name}
          onChange={(event) => {
            setName(event.target.value);
          }}
        />
        <span className="form__input-name-error"></span>
      </label>
      <label className="popup__label">
        <input
          className="popup__input popup__input_type_description"
          id="owner-description"
          maxLength="200"
          minLength="2"
          name="userDescription"
          placeholder="Sobre mim"
          required
          type="text"
          value={about} // Bind description to input
          onChange={(event) => {
            setAbout(event.target.value);
          }}
        />
        <span className="popup__error" id="owner-description-error"></span>
      </label>
      <button className="button popup__button" type="submit">
        Save
      </button>
    </PopupWithForm>
  );
}
